mapper.update:

UPDATE COMPANY_INFO
SET COM_NUMBER=#{comNumber}
	, NUM=#{num}
	, COM_NAME=#{comName}
	, COM_CI=#{comCi}
	, COM_EMAIL=#{comEmail}
	, CEO_NAME=#{ceoName}
	, ZIP_CODE=#{zipCode}
	, ADDRESS1=#{address1}
	, ADDRESS2=#{address2}
	, NOTE=#{note}
	, CEO_PHONE=#{ceoPhone}
			WHERE COM_NUMBER=#{comNumber}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT COM_NUMBER , NUM , COM_NAME , COM_CI , COM_EMAIL , CEO_NAME , ZIP_CODE , ADDRESS1 , ADDRESS2 , NOTE , CEO_PHONE FROM COMPANY_INFO WHERE 1=1
AND COM_NUMBER = #{searchKeyword}
AND NUM LIKE '%' || #{searchKeyword} || '%'
ORDER BY COM_NUMBER DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM COMPANY_INFO WHERE 1=1
AND COM_NUMBER = #{searchKeyword}
AND NUM LIKE '%' || #{searchKeyword} || '%'
