mapper.insert:

INSERT INTO NOTIC 
	( NUM
	  , EMP_NUMBER
	  , COM_NUMBER
	  , DEP_NUMBER
	  , TITLE
	  , CONTENT
	  , DATES )
VALUES ( #{num}
	  , #{empNumber}
	  , #{comNumber}
	  , #{depNumber}
	  , #{title}
	  , #{content}
	  , #{dates} )

mapper.update:

UPDATE NOTIC
SET NUM=#{num}
	, EMP_NUMBER=#{empNumber}
	, COM_NUMBER=#{comNumber}
	, DEP_NUMBER=#{depNumber}
	, TITLE=#{title}
	, CONTENT=#{content}
	, DATES=#{dates}
			WHERE NUM=#{num}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT NUM , EMP_NUMBER , COM_NUMBER , DEP_NUMBER , TITLE , CONTENT , DATES FROM NOTIC WHERE 1=1
AND NUM = #{searchKeyword}
AND EMP_NUMBER LIKE '%' || #{searchKeyword} || '%'
ORDER BY NUM DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM NOTIC WHERE 1=1
AND NUM = #{searchKeyword}
AND EMP_NUMBER LIKE '%' || #{searchKeyword} || '%'
