mapper.update:

UPDATE DEPARTMENT_INFO
SET DEP_NUMBER=#{depNumber}
	, COM_NUMBER=#{comNumber}
	, DEP_NAME=#{depName}
			WHERE DEP_NUMBER=#{depNumber}

mapper.select:
SELECT * FROM (
	SELECT A.*, ROWNUM RNUM FROM (
				SELECT
								DEP_NUMBER
								, COM_NUMBER
								, DEP_NAME
						FROM DEPARTMENT_INFO
				WHERE 1=1

					AND
						DEP_NUMBER = #{searchKeyword}

					AND
						COM_NUMBER LIKE '%' || #{searchKeyword} || '%'


					ORDER BY 
						DEP_NUMBER DESC


	) A WHERE ROWNUM <= #{lastIndex}
)WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM DEPARTMENT_INFO WHERE 1=1
AND DEP_NUMBER = #{searchKeyword}
AND COM_NUMBER LIKE '%' || #{searchKeyword} || '%'
